$.validator.addMethod("checkRefundIdNo", function(b, a) {
    let idType = $("#idType").val();
    if (b == "" || b == null) {
        $.validator.messages.checkRefundIdNo = "请输入开户人证件号码";
        return false
    }
    if (idType == 1) {
        if (b.length != 18) {
            $.validator.messages.checkRefundIdNo = "请输入有效的18位二代身份证号码";
            return false
        }
        if (!isIdCardConfirm(b)) {
            $.validator.messages.checkRefundIdNo = "二代身份证号码格式不正确";
            return false
        }
    }
    if (idType == 2) {
        if (!isLetterDigitBracket(b)) {
            $.validator.messages.checkRefundIdNo = "护照请输入数字字母或括号";
            return false
        }
    }
    if (idType >= 7 && idType <= 9) {
        $.validator.messages.checkRefundIdNo = "请输入正确证件号码";
        if (b.length != 18) {
            return false
        }
        if (b.substr(0, 6) !== "8" + (idType - 6) + "0000") {
            return false
        }
    }
    return true
}, $.validator.format("{0}"));
