function isFunction(a) {
    if (a instanceof Function) {
        return true
    }
    return false
}

function showMsg(e, a, d, c, b) {
    layer.open({
        title: e,
        btn: a,
        closeBtn: 0,
        icon: d,
        content: c,
        btn1: function() {
            layer.closeAll();
            if (isFunction(b)) {
                b()
            }
        }
    })
}

function showMsgOk(c, d, b, a) {
    showMsg(c, [d], 1, b, a)
}

function showMsgWarn(c, d, b, a) {
    showMsg(c, [d], 0, b, a)
}

function showMsgError(c, d, b, a) {
    showMsg(c, [d], 2, b, a)
}

function showMsgAsk(d, a, c, b) {
    layer.open({
        title: d,
        btn: a == "cn" ? ["确定", "取消"] : ["OK", "Cancel"],
        closeBtn: 0,
        icon: 3,
        content: c,
        btn1: function() {
            layer.closeAll();
            if (isFunction(b)) {
                b()
            }
        },
        btn2: function() {
            layer.closeAll()
        }
    })
};